const link=document.querySelector(".contacts-btn"),popup=document.querySelector(".modal"),close=document.querySelector(".btn-close"),form=popup.querySelector("form"),username=popup.querySelector("[name=username]"),email=popup.querySelector("[name=email]"),text=popup.querySelector("[name=text]"),storage=localStorage.getItem("username");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),storage?(username.value=storage,email.focus()):username.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(e){email.value&&text.value?localStorage.setItem("username",username.value):(e.preventDefault(),popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){"Escape"===e.key&&popup.classList.contains("modal-show")&&(e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error"))});
